mapper.insert:
INSERT INTO SAE_GOREVIEW(
			GW_BNO
		  , GW_PNO
		  , GW_TITLE
		  , GW_CONTENT
		  , GW_SCORE
		  , GW_WRITER
		  , GW_FILE )
	  VALUES( SAE_GOREVIEW_SEQ.NEXTVAL
	  , #{gwPno}
	  , #{gwTitle}
	  , #{gwContent}
	  , #{gwScore}
	  , #{gwWriter}
	  , #{gwFile} )

mapper.update:
UPDATE SAE_GOREVIEW
SET GW_TITLE = #{gwTitle},
	GW_CONTENT = #{gwContent},
	GW_SCORE = #{gwScore},
	GW_FILE = #{gwFile}
WHERE GW_BNO = #{gwBno}
