mapper.update:
update sae_book set
bk_inwon=#{bk_inwon},
bk_pdate=#{bk_pdate},
bk_name =#{bk_name},
bk_tel=#{bk_tel}
where bk_bno=#{bk_bno}

mapper.select:
select bk_bno, bk_pno,bk_name, bk_inwon, bk_pdate, pg_name 
from sae_book join sae_program 
on bk_pno = pg_bno 
where bk_id = #{bk_id} 
and bk_type = #{bk_type}
