mapper.select:
SELECT * FROM SAE_COUPON
WHERE COUPON_ID = #{userId}
ORDER BY COUPON_PRICE ASC

SELECT COUPON_USE 
FROM SAE_COUPON 
WHERE COUPON_BNO = #{bno}

mapper.insert:
INSERT INTO SAE_COUPON(COUPON_BNO,
	  				   COUPON_ID,
	   				   COUPON_NAME,
	  				   COUPON_PRICE
	  				   )
	  			VALUES( sae_coupon_seq.nextval,
	  					#{coupon_id},
	  					#{coupon_name},
	  					#{coupon_price}
	  					)
